import React, { useState, useEffect } from 'react';
import './App.css';
import WeatherForecast from './WeatherForecast';
import MobileWeather from './Components/MobileWeather';
import WeatherInput from './Components/weatherInput';

function App() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState(undefined);
  const [todayWeather, setTodayWeather] = useState({temp:''});
  const [nextTwoDaysWeather, setNextTwoDaysWeather] = useState([]);

  async function getWeatherData(location) {
    try {
      console.log(location);
      const response = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&appid=094b8fd5531e1870279f58b241957f6f`);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setData(data);
      setLocation(data.city.name);

      // Extract today's weather details
      const today = new Date().getDate();
      const todayWeather = data.list.find(item => new Date(item.dt * 1000).getDate() === today);

      // Extract next two days' weather details
      const nextTwoDaysWeather = [];
      for (let i = 1; i <= 2; i++) {
        const day = new Date();
        day.setDate(today + i);
        const dayWeather = data.list.filter(item => new Date(item.dt * 1000).getDate() === day.getDate());
        nextTwoDaysWeather.push(dayWeather);
      }

      setTodayWeather(todayWeather.main.temp);
      setNextTwoDaysWeather(nextTwoDaysWeather);

    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
  }

  return (
    <div className="App">
      <div className='title'>Weather Forecast for {location}</div>
      <WeatherInput getWeatherData={getWeatherData}/>
      <MobileWeather location={location} todayWeather={todayWeather}/>
      <WeatherForecast nextTwoDaysWeather={nextTwoDaysWeather}/>
    </div>
  );
}

export default App;
