import React, { useState } from "react";
import './WeatherInput.css';

export default function WeatherInput() {
    const [location, setLocation] = useState('');

    function handleInput(event) {
        event.preventDefault(); // Prevents the default form submission
        getweatherdata(location);
        setLocation('');
    }

    async function getweatherdata(location){
        const response = await fetch('https://api.openweathermap.org/data/2.5/forecast?q={location}&appid=094b8fd5531e1870279f58b241957f6f');
        const data = await response.json();
        console.log(data);
    }

    return (
        <div className="input-container">
            <form className="Weather-input" onSubmit={handleInput}>
                <input
                    type='text'
                    placeholder="Enter Location name...."
                    value={location}
                    name="location"
                    onChange={(e) => {
                        setLocation(e.target.value);
                    }}
                />
                <button type="submit">
                    <span className="material-symbols-outlined">search</span>
                </button>
            </form>
        </div>
    );
}
