import React,{useState} from 'react';
import './App.css';
import WeatherForecast from './WeatherForecast';
import MobileWeather from './Components/MobileWeather';
import WeatherInput from './Components/weatherInput';

function App() {
  const [data,setData]=useState({});
  const [location,setLocation]=useState(undefined);

  async function getWeatherData(location) {
    try {
      console.log(location);
      const response = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${location}&appid=094b8fd5531e1870279f58b241957f6f`);

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      setData(data);
      setLocation(data.city.name);
      console.log(data);
    } 
    catch (error) {
      console.error('Error fetching weather data:', error);
    }
  }

  return (
    <div className="App">
      <div className='title'>Weather Forecast for </div>
      <WeatherInput getWeatherData={getWeatherData}/>
      <MobileWeather location={location}/>
      <WeatherForecast/>
    </div>
  );
}

export default App;
